#define PB_ZERO(returnVal, name, ...) name = NULL;

#define PB_LOAD_FUNCTION(returnVal, name, ...)                             \
	{                                                                      \
		if (m_ModuleVM.hModule && name == NULL)                            \
			name = (_Sys##name)GetProcAddress(m_ModuleVM.hModule, #name);  \
		if (m_ModuleSHR.hModule && name == NULL)                           \
			name = (_Sys##name)GetProcAddress(m_ModuleSHR.hModule, #name); \
		if (m_ModuleDW.hModule && name == NULL)                            \
			name = (_Sys##name)GetProcAddress(m_ModuleDW.hModule, #name);  \
		if (m_ModuleSYS.hModule && name == NULL)                           \
			name = (_Sys##name)GetProcAddress(m_ModuleSYS.hModule, #name); \
	}

#define PB_DEFINE_TYPEDEF(r, name, ...)            \
	typedef r(__stdcall *_Sys##name)(__VA_ARGS__); \
	__declspec(selectany) _Sys##name name = NULL;

